// function for active and non-active lengan pendek
function setActiveLenganPendek() {
    $('#lengan__pendek').removeClass('btn btn-outline-light');
    $('#lengan__pendek').addClass('btn-warning btn');
}

function nonActiveLenganPendek() {
    $('#lengan__pendek').addClass('btn btn-outline-light');
    $('#lengan__pendek').removeClass('btn-warning btn');
}
// end for lengan pendek

